contracts/PredixPoll.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// this contract will support the creation of both public and private polls.
//For private polls whitelist is set at creation, votes are stored in an array for each poll option.

contract PredixPolls {

    //type of poll: public are open and privat are whitelisted.
    enum PollType {Private, Public} 

    struct Poll {
        uint id;
        string title;
        string[] options;
        uint[] votes;
        address creator;
        PollType pollType;
        bool isClosed;

        //mappings for the per poll acccess control and vote tracking
        mapping(address => bool) hasVoted;
        mapping(address => bool) whitelist;
    }

    uint public pollCount;
    mapping(uint256 => Poll) internal polls;
   // mapping(uint => mapping(address => bool)) public hasVoted;

    event PollCreated(uint256 indexed pollId, string title, string[] options, PollType pollType);
    event Voted(uint256 indexed pollId, uint256 optionIndex, address voter);
    event PollClosed(uint indexed pollId);

    modifier onlyCreator(uint _pollId) {
        require(msg.sender == polls[_pollId].creator, "Not poll creator");
        _;
    }

    // @notice Create a new poll. For Private polls, provide a non-empty whitelist.
    // @param _title The poll question.
    // @param _options Array of answer options (at least two).
    // @param _pollType 0 for Public, 1 for Private.
    // @param _whitelist Addresses allowed to vote (only needed for Private polls)


    function createPoll(string memory _title, string[] memory _options, PollType _pollType, address[] memory _whitelist) public {
        require(_options.length >= 2, "Must have at least 2 options");

        pollCount++;
        Poll storage newPoll = polls[pollCount];
        newPoll.id = pollCount;
        newPoll.title = _title;
        newPoll.options = _options;
        //newPoll.votes = new uint[](_options.length);
        newPoll.creator = msg.sender;
        newPoll.pollType = _pollType;
        newPoll.isClosed = false;

        //initialize votes array with zeros for each option.
        newPoll.votes = new uint256[](_options.length);

        //if the poll is private we requre and set whitelist.
        if (_pollType == PollType.Private){
            require(_whitelist.length > 0, "whitelist is required for private polls");
            for (uint256 i = 0; i < _whitelist.length; i++){
                newPoll.whitelist[_whitelist[i]] = true;
            }

        }

        emit PollCreated(pollCount, _title, _options,_pollType);
    }

     /// @notice Cast a vote in a poll.
    /// @param _pollId The poll identifier.
    /// @param _optionIndex The index of the chosen option.

    //function vote(uint _pollId, uint _optionIndex) public {
        //Poll storage poll = polls[_pollId];

       // require(!polls[_pollId].isClosed, "Poll is closed");
        //require(!hasVoted[_pollId][msg.sender], "Already voted");
        //require(_optionIndex < polls[_pollId].options.length, "Invalid option");

        //For the private Polls
      //  if (poll.pollType == pollType.Private){
           // require(poll.whitelist[msg.sender], "Not Authorized to vote in this poll");
       // }


       // polls[_pollId].votes[_optionIndex]++;
        //polls.votes[_optionIndex ] += 1; this functions the same as the line above
        //hasVoted[_pollId][msg.sender] = true;

        //emit Voted(_pollId, _optionIndex, msg.sender);
    //}
    /// @notice Cast a vote in a poll.
    /// @param _pollId The poll identifier.
    /// @param _optionIndex The index of the chosen option.
    function vote(uint256 _pollId, uint256 _optionIndex) external {
        Poll storage poll = polls[_pollId];

        require(!poll.isClosed, "Poll is closed");
        require(_optionIndex < poll.options.length, "Invalid option");
        require(!poll.hasVoted[msg.sender], "Already voted");

        // For private polls, verify that sender is whitelisted.
         if (poll.pollType == PollType.Private) {
            require(poll.whitelist[msg.sender], "Not authorized to vote in this poll");
            }

         poll.votes[_optionIndex] += 1;
         poll.hasVoted[msg.sender] = true;

       emit Voted(_pollId, _optionIndex, msg.sender);
    }




     /// @notice Close a poll to prevent further voting.
    /// @param _pollId The poll identifier.

    //function closePoll(uint256 _pollId) public onlyCreator(_pollId) {
       // polls[_pollId].isClosed = true;
      //  emit PollClosed(_pollId);
    //}

     function closePoll(uint256 _pollId) external onlyCreator(_pollId){
        Poll storage poll = polls[_pollId];
        require(msg.sender == poll.creator, "Only creator can close poll");
        require(!poll.isClosed, "Poll already closed");
        poll.isClosed = true;
        emit PollClosed(_pollId);
    }

     //getting the votes of the closed id

    function getPollResults(uint256 _pollId) public view returns (string memory title, uint256[] memory votes) {
        //Poll storage poll = Polls[_pollId];
        //Poll storage poll = polls[_pollId];

        return (polls[_pollId].title, polls[_pollId].votes);
    }
}

contracts/predixPredictions.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

contract PredixPredictionMarket {
    struct Market {
        uint256 id;
        string question;
        uint256 pollId;           // Set to 0 for standalone markets.
        string[] options;         // Options to bet on.
        uint256[] totalBets;      // Total Ether bet per option.
        uint256 fund;             // Total Ether collected for this market.
        bool resolved;
        uint256 winningOption;    // Winning option index.
        // Tracks each user's bet for each option.
        mapping(uint256 => mapping(address => uint256)) bets;
    }

    uint256 public marketCount;
    mapping(uint256 => Market) internal markets;

    event MarketCreated(uint256 indexed marketId, string question, uint256 pollId, string[] options);
    event BetPlaced(uint256 indexed marketId, uint256 optionIndex, address indexed bettor, uint256 amount);
    event MarketResolved(uint256 indexed marketId, uint256 winningOption);

    /// @notice Create a new prediction market.
    /// @param _question The market question.
    /// @param _pollId The associated poll ID (set to 0 if standalone).
    /// @param _options Array of prediction options.
    function createMarket(
        string memory _question,
        uint256 _pollId,
        string[] memory _options
    ) external {
        require(_options.length >= 2, "At least two options required");
        marketCount++;
        Market storage market = markets[marketCount];
        market.id = marketCount;
        market.question = _question;
        market.pollId = _pollId;
        market.options = _options;
        market.totalBets = new uint256[](_options.length);
        market.resolved = false;
        market.winningOption = 0;
        market.fund = 0;

        emit MarketCreated(marketCount, _question, _pollId, _options);
    }

    /// @notice Place a bet on a prediction market option.
    /// @param _marketId The market identifier.
    /// @param _optionIndex The option index to bet on.
    function placeBet(uint256 _marketId, uint256 _optionIndex) external payable {
        Market storage market = markets[_marketId];
        require(!market.resolved, "Market is resolved");
        require(_optionIndex < market.options.length, "Invalid option");
        require(msg.value > 0, "Bet amount must be > 0");

        market.totalBets[_optionIndex] += msg.value;
        market.bets[_optionIndex][msg.sender] += msg.value;
        market.fund += msg.value;

        emit BetPlaced(_marketId, _optionIndex, msg.sender, msg.value);
    }

    /// @notice Resolve a prediction market by declaring the winning option.
    /// @param _marketId The market identifier.
    /// @param _winningOption The index of the winning option.
    function resolveMarket(uint256 _marketId, uint256 _winningOption) external {
        Market storage market = markets[_marketId];
        require(!market.resolved, "Market already resolved");
        require(_winningOption < market.options.length, "Invalid option");

        market.resolved = true;
        market.winningOption = _winningOption;

        emit MarketResolved(_marketId, _winningOption);
    }

    /// @notice Claim reward for winning bets in a resolved market.
    /// @param _marketId The market identifier.
    function claimReward(uint256 _marketId) external {
        Market storage market = markets[_marketId];
        require(market.resolved, "Market not resolved yet");
        uint256 winningOption = market.winningOption;
        uint256 userBet = market.bets[winningOption][msg.sender];
        require(userBet > 0, "No winning bet to claim");

        // Calculate reward: user's share of the total fund based on their bet proportion.
        uint256 totalWinningBets = market.totalBets[winningOption];
        uint256 reward = (userBet * market.fund) / totalWinningBets;

        // Prevent re-entrancy by zeroing the user bet before transfer.
        market.bets[winningOption][msg.sender] = 0;
        payable(msg.sender).transfer(reward);
    }
}

